import { Box, Chip, Typography } from '@mui/material';
import React, {useState, useEffect} from 'react';
import {useParams} from 'react-router-dom';
import { ApiService } from '../../Service/api.service';
import ReactPlayer from 'react-player';
import { Tag } from '@mui/icons-material';

const VideoDetail = () => {
    const [videoDetail, setVideoDetail] = useState(null)
    const { id } =  useParams()

    useEffect(() => {
        const getData = async () => {
            try {
                const data = await ApiService.fetching(`videos?part=snippet,statistics&id=${id}`)
                setVideoDetail(data.items[0])
            } catch (error) {
                console.log(error);
            }
        }
        getData()
    }, [id])

    // const {
    //     snippet: { title, channelTitle, channelId, description, tags, thumnails },
    //     statistics: { viewCount, likeCount, commentCount},
    // } = videoDetail

return (
    <Box minHeight={'90vh'} mb={10}>
        <Box display={'flex'}>
            <Box width={'75%'}>
                <ReactPlayer url={`https://www.youtube.com/watch?v=${id}`} 
                className='react-player' 
                controls />
                {videoDetail.snippet.tags.map((item, idx) => {
                    <Chip 
                        label={item}
                        key={idx}
                        sx={{ marginTop: '10px', cursor: 'pointer', ml: '10px'}}
                        deleteIcon={<Tag />}
                        onDelete={() => {}}
                        variant= 'outlined'
                    />
                })}
                <Typography variant='h5' fontWeight='bold' p={2}>
                        {videoDetail.snippet.title}
                </Typography>
            </Box>
            <Box width={'25%'}>suggested video</Box>
        </Box>
    </Box>
)
}

export default VideoDetail
